mapper.insert:
/* com.opennote.standard.mapper.basic.atchFileMng.putAtchFile */

	SELECT IFNULL(CAST(MAX(D.FILE_SEQO) AS INT) + 1, 0) FROM T_ATCH_FILE_MNG D WHERE D.ATCH_FILE_ID = #{atchFileId}


INSERT INTO T_ATCH_FILE_MNG (
	ATCH_FILE_ID
	, FILE_SEQO
	, PHCL_FILE_PTH_NM
	, PHCL_FILE_NM
	, RL_FILE_NM
	, FILE_EXTN_NM
	, FILE_SZ_VAL
	, FILE_TP_NM
	, IMG_WDTH_SZ_VAL
	, IMG_HGHT_SZ_VAL
	, FILE_BYTE										   
	, REGR_SERNO
	, REG_DT
	, DEL_YN
) VALUES (
	#{atchFileId}
	, #{fileSeqo}
	, #{phclFilePthNm}
	, #{fileNmPhclFileNm}
	, #{fileRlNm}
	, #{fileFextNm}
	, #{fileSizeVal}
	, #{fileTpNm}
	, #{imgSqrVal}
	, #{imgHghtVal}
	, #{fileByte}
    , #{loginSerno}
	, NOW()
	, 'N'
)

/* com.opennote.standard.mapper.basic.atchFileMng.putAtchFile_old */

	SELECT IFNULL(CAST(MAX(D.FILE_SEQO) AS INT) + 1, 0) FROM T_ATCH_FILE_MNG D WHERE D.ATCH_FILE_ID = #{atchFileId}


INSERT INTO T_ATCH_FILE_MNG (
	ATCH_FILE_ID
	, FILE_SEQO
	, PHCL_FILE_PTH_NM
	, PHCL_FILE_NM
	, RL_FILE_NM
	, FILE_EXTN_NM
	, FILE_SZ_VAL
	, FILE_TP_NM
	, IMG_WDTH_SZ_VAL
	, IMG_HGHT_SZ_VAL
	, FILE_BYTE

	, REGR_SERNO


    , REGR_SERNO										   

	, REG_DT
	, DEL_YN
) VALUES (
	#{atchFileId}
	, #{fileSeqo}
	, #{phclFilePthNm}
	, #{fileNmPhclFileNm}
	, #{fileRlNm}
	, #{fileFextNm}
	, #{fileSizeVal}
	, #{fileTpNm}
	, #{imgSqrVal}
	, #{imgHghtVal}
	, #{fileByte}

    , #{loginSerno}										   


    , #{loginSerno}										   

	, NOW()
	, 'N'
)

mapper.update:
/* com.opennote.standard.mapper.basic.atchFileMng.delAtchFile */
UPDATE T_ATCH_FILE_MNG
SET DEL_YN = 'Y'
WHERE ATCH_FILE_ID = #{atchFileId}

		AND FILE_SEQO = #{fileSeqo}

	AND DEL_YN = 'N'

mapper.select:
/* com.opennote.standard.mapper.basic.atchFileMng.getAtchFileId  */
SELECT IFNULL(MAX(CAST(ATCH_FILE_ID  AS UNSIGNED)),0)+1 
  FROM T_ATCH_FILE_MNG
