mapper.insert:

	SELECT NVL(CAST(MAX(D.FILE_SEQO) AS INT) + 1, 0) FROM itsm_atch_file_mng D WHERE D.ATCH_FILE_ID = #{atchFileId}


INSERT INTO itsm_atch_file_mng (
	ATCH_FILE_ID
	, FILE_SEQO
	, PHCL_FILE_PTH_NM
	, PHCL_FILE_NM
	, RL_FILE_NM
	, FILE_EXTN_NM
	, FILE_SZ_VAL
	, FILE_TP_NM
	, IMG_WDTH_SZ_VAL
	, IMG_HGHT_SZ_VAL
	, FILE_BYTE
	, RGTR_SN
	, REG_DT
	, DEL_YN
) VALUES (
	#{atchFileId}
	, #{fileSeqo ,jdbcType=VARCHAR}
	, #{phclFilePthNm}
	, #{fileNmPhclFileNm}
	, #{fileRlNm}
	, #{fileFextNm}
	, #{fileSizeVal}
	, #{fileTpNm}
	, #{imgSqrVal}
	, #{imgHghtVal}
	, #{fileByte}
	, #{loginSerno, jdbcType=VARCHAR}
	, NOW()
	, 'N'
)

mapper.update:
UPDATE itsm_atch_file_mng
SET DEL_YN = 'Y'
WHERE ATCH_FILE_ID = #{atchFileId}

		AND FILE_SEQO = #{fileSeqo}

	AND DEL_YN = 'N'
